    private static $strCache = [];

    public static function gen($len = 10, $type = self::NUM | self::ALPHA) {
        $str = self::getChars($type);
        $strLen = mb_strlen($str);

        $ret = "";
        for ($i = 0; $i < $len; $i++) {
            // non safe rand
            $ret .= mb_substr($str, rand(0, $strLen - 1), 1);
        }
        return $ret;
    }

    static function getBytes($n)
    {
