        }
        else
        {
            return self::gen($n);
        }
    }

    private static function getChars($mask)
    {
        if (isset(static::$strCache[$mask])) {
            return static::$strCache[$mask];
        }

        $str = "";
        if ($mask & self::NUM) {
            $str .= self::__NUM;
        }

        if ($mask & self::ALPHA) {
            $str .= self::__ALPHA;
        }

        if ($mask & self::CHINESE) {
            $str .= self::__CHINESE2000;
        }

        if ($str === "") {
            $str .= self::NUM . self::ALPHA;
        }

        static::$strCache[$mask] = $str;
        return $str;
    }
}